/* Path: HOST/logout
 *  Return: JSON Object that contains 'code' & 'msg',
 *  'session_id' will also be provide for debug purposes
 *
 *  1 -> Successfully logged out
 *  -1 -> You haven't been logged in yet
 */

exports.logout = function(sess, callback){
    if(sess.user_id !== undefined) {
        sess.destroy(function (err) {
            callback({
                'code': "1",
                'msg': "Successfully logged out",
                'session_id': sess.id
            });
        });
    }else{
        callback({
            'code': "-1",
            'msg': "You haven't been logged in yet",
            'session_id': sess.id
        });
    }
}