var mongoose = require('mongoose');

var userSchema = mongoose.Schema({
    token: String,
    phone_number: String,
    code: String,
    vertify_flag: Boolean,
    hashed_password: String,
    sex: String,
    email: String,
    first_name: String,
    last_name: String,
    driver_flag: Boolean,
    driver_license: String,
    plate_number: String,
    colour: String,
    car_model: String,
    salt: String,
    has_avatar: Number
});

var chatHistorySchema = mongoose.Schema({
    sender_user_id: String,
    receiver_user_id: String,
    message: String
});

mongoose.connect('mongodb://vgo:thomas@ec2-52-40-59-253.us-west-2.compute.amazonaws.com:27017/vgoDB');
var User = mongoose.model('user', userSchema);
var ChatHistory = mongoose.model('chat_history', chatHistorySchema);

module.exports = {
    User: User,
    ChatHistory: ChatHistory
};